mapper.select:
select c.id, c.book_id as Bookid, c.user_id as Userid, u.name as username, c.parent_id as Parentid, c.content, c.create_time as createTime, c.is_deleted as isDeleted from comment c left join user u on c.user_id = u.id where c.is_deleted = 0
AND c.book_id = #{BookId}
order by c.create_time desc

mapper.update:
UPDATE comment
SET is_deleted = 1
WHERE id = #{id}
